declare @sql nvarchar(max);
declare @params nvarchar(max);
declare @sql_instance varchar(255);
declare @start_time_utc datetime2;
declare @end_time_utc datetime2;

set @sql_instance = '$server';
set @start_time_utc = $__timeFrom();
set @end_time_utc = $__timeTo();
set @params = N'@sql_instance varchar(255), @start_time_utc datetime2, @end_time_utc datetime2';

set quoted_identifier off;
set @sql = "
set nocount on;
select	[time] = DATEADD(mi, DATEDIFF(mi, getdate(), getutcdate()), h.collection_time),
				[SqlServer] = h.physical_memory_in_use_kb,
				[Total_Ram] = asi.total_physical_memory_kb,
				[Available_Memory] = h.available_physical_memory_kb
from dbo.all_server_volatile_info_history h
join dbo.all_server_stable_info asi	on asi.srv_name = h.srv_name
where h.collection_time between DATEADD(mi, DATEDIFF(mi, GETUTCDATE(), GETDATE()), @start_time_utc) and DATEADD(mi, DATEDIFF(mi, GETUTCDATE(), GETDATE()), @end_time_utc)
and h.srv_name = @sql_instance
order by time
"
set quoted_identifier on;

exec dbo.sp_executesql @sql , @params, @sql_instance, @start_time_utc, @end_time_utc;

