declare @sql nvarchar(max);
declare @params nvarchar(max);
declare @server varchar(125);
declare @oncall_team varchar(125);

set @oncall_team = '${oncall_team}';

set @server = case when ltrim(rtrim('$server_input')) <> '' then ltrim(rtrim('$server_input')) else null end;
set @params = N'@server varchar(255), @oncall_team varchar(125)';

set quoted_identifier off;
set @sql = "
/* $__dashboard */
select	[team_name], [description], [team_lead_email], [team_email], [team_lead_slack_account], 
		[team_slack_channel], [pagerduty_service_key], [alert_method], [created_by], [created_date_utc]
from dbo.sma_oncall_teams ot
where 1=1
"+(case when @oncall_team = '_All_' then '--' else '' end)+"and team_name = @oncall_team
";
set quoted_identifier off;

exec dbo.sp_executesql @sql, @params, @server, @oncall_team;

declare @sql nvarchar(max);
declare @params nvarchar(max);
declare @server varchar(125);
declare @oncall_team varchar(125);
declare @alert_id bigint;
declare @alert_key varchar(225);

set @oncall_team = '${oncall_team}';

set @server = case when ltrim(rtrim('$server_input')) <> '' then ltrim(rtrim('$server_input')) else null end;
set @alert_id = case when ltrim(rtrim('$alert_id')) <> '' then ltrim(rtrim('$alert_id')) else null end;
set @alert_key = case when ltrim(rtrim('$alert_key')) <> '' then ltrim(rtrim('$alert_key')) else null end;

set @params = N'@server varchar(255), @oncall_team varchar(125), @alert_id bigint, @alert_key varchar(225)';

set quoted_identifier off;
set @sql = "
/* $__dashboard */
select	alert_key, created_date_utc, alert_owner_team, [state], severity, [log_header] = header,
				[last_log] = log_time_utc, logged_by, [log_details] = [description],
				suppress_start_date_utc, suppress_end_date_utc, frequency_minutes, [alert_id] = a.id
from dbo.sma_alert a
cross apply (
	select	"+(case when @alert_id is not null or @alert_key is not null then '--' else '' end)+"top 1
			*
	from dbo.sma_alert_history ah
	where 1=1
	and ah.alert_id = a.id
	and ah.alert_id_part_no = a.id_part_no
) ah
where 1=1
and a.state <> 'Resolved'
"+(case when @oncall_team = '_All_' then '--' else '' end)+"and alert_owner_team = @oncall_team
"+(case when @alert_id is null then '--' else '' end)+"and a.id = @alert_id and a.id_part_no = @alert_id % 10
"+(case when @alert_key is null then '--' else '' end)+"and a.alert_key = @alert_key
order by id, id_part_no
";
set quoted_identifier off;

exec dbo.sp_executesql @sql, @params, @server, @oncall_team, @alert_id, @alert_key;

