set @alert_key = case when ltrim(rtrim('$alert_key')) <> '' then ltrim(rtrim('$alert_key')) else null end;

set @params = N'@server varchar(255), @oncall_team varchar(125), @alert_id bigint, @alert_key varchar(225)';

set quoted_identifier off;
set @sql = "
declare @all_server_dashboard varchar(500);
declare @core_health_panel varchar(500);
select @all_server_dashboard = p.param_value from dbo.sma_params p where p.param_key = 'GrafanaDashboardPortal';
select @core_health_panel = p.param_value from dbo.sma_params p where p.param_key = 'url_all_servers_core_health_metrics_dashboard_panel';

select	/* $__dashboard */
				a.alert_key, created_date_utc, alert_owner_team, [state], severity, [log_header] = header,
				[last_log] = log_time_utc, logged_by, [log_details] = [description],
				suppress_start_date_utc, suppress_end_date_utc, frequency_minutes, [alert_id] = a.id

				,[url] = case when a.alert_key in ('Alert-Cpu','Alert-AvailableMemory','Alert-DiskLatency')
						then @core_health_panel
						else p.alert_data_url
						end
from dbo.sma_alert a
cross apply (
	select	"+(case when @alert_id is not null or @alert_key is not null then '--' else '' end)+"top 1
			*
	from dbo.sma_alert_history ah
	where 1=1
	and ah.alert_id = a.id
	and ah.alert_id_part_no = a.id_part_no
	order by ah.log_time_utc desc
) ah
left join
	(
		select	p.param_key, p.param_value, 
				alert_data_url = p.param_value, 
				alert_key = case when param_key = 'url_all_servers_alert_history_dashboard_panel' then 'Alert-SqlErrorLog'
								when param_key = 'url_all_servers_alwayson_latency_dashboard_panel' then 'Alert-AgLatency'
								when param_key = 'url_all_servers_backups_ag_dbs_dashboard_panel' then 'Alert-AgDbBackupIssue'
								when param_key = 'url_all_servers_backups_nonag_dbs_dashboard_panel' then 'Alert-NonAgDbBackupIssue'
								when param_key = 'url_all_servers_core_health_metrics_dashboard_panel' then 'CoreHealthPanel'
								when param_key = 'url_all_servers_disk_utilization_dashboard_panel' then 'Alert-DiskSpace'
								when param_key = 'url_all_servers_login_expiry_dashboard_panel' then 'Alert-LoginExpiry'
								when param_key = 'url_all_servers_logspace_utilization_dashboard_panel' then 'Alert-LogSpace'
								when param_key = 'url_all_servers_offline_aliases_dashboard_panel' then 'Alert-OfflineServerAlias'
								when param_key = 'url_all_servers_offline_servers_dashboard_panel' then 'Alert-OfflineServer'
								when param_key = 'url_all_servers_sqlagent_service_dashboard_panel' then 'Alert-OfflineAgent'
								when param_key = 'url_all_servers_sqlmonitor_jobs_dashboard_panel' then 'Alert-SqlMonitorJobs'
								when param_key = 'url_all_servers_tempdb_utilization_dashboard_panel' then 'Alert-Tempdb'
								else null
								end
		from dbo.sma_params p where p.param_key like 'url_all_servers_%'
	) p on p.alert_key = a.alert_key
where 1=1
and a.state <> 'Resolved'
"+(case when @oncall_team = '_All_' then '--' else '' end)+"and alert_owner_team = @oncall_team
"+(case when @alert_id is null then '--' else '' end)+"and a.id = @alert_id and a.id_part_no = @alert_id % 10
"+(case when @alert_key is null then '--' else '' end)+"and a.alert_key = @alert_key
order by id, id_part_no
";
set quoted_identifier off;

exec dbo.sp_executesql @sql, @params, @server, @oncall_team, @alert_id, @alert_key;

