declare @sql nvarchar(max);
declare @params nvarchar(max);
declare @sql_instance varchar(255);
declare @start_time_utc datetime2;
declare @end_time_utc datetime2;
declare @crlf nchar(2) = nchar(13)+nchar(10);

declare @database nvarchar(500) = '$database';
declare @trend_by varchar(20) = '$trend_by'; -- Hour,Day
declare @hour_of_day int = ${hour_of_day};
declare @grouping_key varchar(500) = '${grouping_key:raw}'; --Login,Program,Host,Database
declare @group_by_sql varchar(500) = '';
declare @group_by_metric varchar(500);
declare @login varchar(125) = '$login';
declare @program varchar(500) = '$program';
set @login = case when ltrim(rtrim(@login)) = '' then null else @login end;
set @program = case when ltrim(rtrim(@program)) = '' then null else @program end;

set @sql_instance = '$server';
set @start_time_utc = $__timeFrom();
set @end_time_utc = $__timeTo();
set @params = N'@start_time_utc datetime2, @end_time_utc datetime2, @database nvarchar(500), @login varchar(125), @program varchar(500), @hour_of_day int';
set @database = case when ltrim(rtrim(@database)) = '__All__' then null else @database end;
if @grouping_key like '%Login%'
begin
		set @group_by_sql += ', username'
		set @group_by_metric = case when @group_by_metric is null then 'username' else @group_by_metric +'+'' || ''+username' end;
end
if @grouping_key like '%Program%'
begin
		set @group_by_sql += ', client_app_name'
		set @group_by_metric = case when @group_by_metric is null then 'client_app_name' else @group_by_metric +'+'' || ''+client_app_name' end;
end
if @grouping_key like '%Host%'
begin
		set @group_by_sql += ', client_hostname'
		set @group_by_metric = case when @group_by_metric is null then 'client_hostname' else @group_by_metric +'+'' || ''+client_hostname' end;
end
if @grouping_key like '%Database%'
begin
		set @group_by_sql += ', database_name'
		set @group_by_metric = case when @group_by_metric is null then 'database_name' else @group_by_metric +'+'' || ''+database_name' end;
end

set quoted_identifier off;
set @sql = "/* SQLMonitor XEvent Dashboard */
set nocount on;			

;with t_xevent_metrics as 
(
	select --top 1000 
		[time] = "+(case when @trend_by = 'Daily' 
										then 'cast(convert(date,rc.event_time) as datetime)' 
										else 'dateadd(hour,DATEPART(hour,rc.event_time),cast(convert(date,rc.event_time) as datetime))' 
								end)+@group_by_sql+" ,cpu_time_ms ,logical_reads ,result
	from $xevent_table_name rc with (nolock)
	where 1=1
	and rc.event_time between DATEADD(mi, DATEDIFF(mi, GETUTCDATE(), GETDATE()), @start_time_utc) and DATEADD(mi, DATEDIFF(mi, GETUTCDATE(), GETDATE()), @end_time_utc)
	"+(case when @database is null then '-- ' else '' end)+"and rc.database_name like ('%'+@database+'%')
	"+(case when @login is null then '-- ' else '' end)+"and rc.username = @login
	"+(case when @program is null then '-- ' else '' end)+"and rc.client_app_name = @program
	"+(case when @trend_by = 'Daily' then '-- ' else '' end)+"and DATEPART(hour,rc.event_time) = @hour_of_day
)
select 	[time] = DATEADD(mi, DATEDIFF(mi, getdate(), getutcdate()), [time]) ,
				[metric] = "+@group_by_metric+" ,cpu_time_ms = sum(cpu_time_ms) --,logical_reads ,result
from t_xevent_metrics rc
where 1=1
group by [time] "+@group_by_sql+"
order by [time]
"
set quoted_identifier on;
print @sql

--if (@sql_instance = SERVERPROPERTY('SERVERNAME'))
if ($is_local = 1)
  exec dbo.sp_executesql @sql, @params, @start_time_utc, @end_time_utc, @database, @login, @program, @hour_of_day;
else
  exec [$server].[$dba_db].dbo.sp_executesql @sql, @params, @start_time_utc, @end_time_utc, @database, @login, @program, @hour_of_day;

