declare @sql nvarchar(max);
declare @params nvarchar(max);
declare @sql_instance varchar(255) = '$server';
declare @start_time_utc datetime2;
declare @end_time_utc datetime2;
declare @delta_days_threshold int = ${delta_days_threshold};
declare @buffer_days int = 2;
declare @database varchar(255) = '$database';
declare @page_no int;
declare @page_size int;
declare @table_name varchar(255);

set @database = case when ltrim(rtrim(@database)) = '__All__' then null else @database end;
set @start_time_utc = $__timeFrom();
set @end_time_utc = $__timeTo();
if len(ltrim(rtrim('$page_no'))) > 0 and (case when '$page_no' like '%[^0-9.]%' then 'invalid' when '$page_no' like '%.%.%' then 'invalid' else 'valid' end) = 'valid'
  set @page_no = convert(int,ltrim(rtrim('$page_no')));
if len(ltrim(rtrim('$page_size'))) > 0 and (case when '$page_size' like '%[^0-9.]%' then 'invalid' when '$page_size' like '%.%.%' then 'invalid' else 'valid' end) = 'valid'
  set @page_size = convert(int,ltrim(rtrim('$page_size')));
if len(ltrim(rtrim('$table_name'))) > 0
  set @table_name = '$table_name'

set @params = N'@database varchar(255), @delta_days_threshold int, @buffer_days int, @page_no int, @page_size int, @table_name varchar(255)';

set quoted_identifier off;
set @sql = "/* SQLMonitor - BlitzIndex Analysis - 0 Delta Rows */
set nocount on;

select b.Priority, b.FindingsGroup, b.Finding, b.DatabaseName, b.URL, b.Details
from $blitz_table_name b
where b.CheckDate = (select top 1 CheckDate from $blitz_table_name order by CheckDate Desc)
and (	case when FindingsGroup in ('Thanks!','Wait Stats')
			then 0
			when FindingsGroup like 'sp_Blitz %'
			then 0
			else 1
			end ) = 1
and FindingsGroup in ('Reliability')
"
set quoted_identifier on;

if ($is_local = 1)
  exec dbo.sp_executesql @sql, @params, @database, @delta_days_threshold, @buffer_days, @page_no, @page_size, @table_name;
else
  exec [$server].[$dba_db].dbo.sp_executesql @sql, @params, @database, @delta_days_threshold, @buffer_days, @page_no, @page_size, @table_name;

