declare @sql nvarchar(max);
declare @params nvarchar(max);
declare @sql_instance varchar(255);
declare @perfmon_host_name varchar(255) = '$host_name';
declare @start_time_utc datetime2;
declare @end_time_utc datetime2;
declare @disk_drive varchar(255) = '$disk_drive';
declare @database varchar(255) = '$database';

set @database = case when ltrim(rtrim(@database)) = '__All__' then null else @database end;
set @disk_drive = case when ltrim(rtrim(@disk_drive)) = '__All__' then null else @disk_drive end;

set @sql_instance = '$server';
--set @perfmon_host_name = '$perfmon_host_name';
set @start_time_utc = $__timeFrom();
set @end_time_utc = $__timeTo();
--set @delta_minutes = $cpu_delta_minutes;
set @params = N'@start_time_utc datetime2, @end_time_utc datetime2, @disk_drive varchar(255), @perfmon_host_name varchar(255), @database varchar(255)';

set quoted_identifier off;
set @sql = "/* SQLMonitor - Disk Space Dashboard */
set nocount on;
declare @_latest_snap datetime2;
declare @_24hour_snapshot datetime2;
declare @_5day_snapshot datetime2;
declare @_10day_snapshot datetime2;
declare @_30day_snapshot datetime2;
declare @_latest_diskspace_snap datetime2;

select @_latest_snap = max(collection_time_utc)
from $fileiostats_table_name fis;

select @_24hour_snapshot = max(collection_time_utc)
from $fileiostats_table_name fis
where fis.collection_time_utc <= dateadd(hour,-24,@_latest_snap);

select @_5day_snapshot = max(collection_time_utc)
from $fileiostats_table_name fis
where fis.collection_time_utc <= dateadd(day,-5,@_latest_snap);

select @_10day_snapshot = max(collection_time_utc)
from $fileiostats_table_name fis
where fis.collection_time_utc <= dateadd(day,-10,@_latest_snap);

select @_30day_snapshot = max(collection_time_utc)
from $fileiostats_table_name fis
where fis.collection_time_utc <= dateadd(day,-30,@_latest_snap);

select top 1 @_latest_diskspace_snap = ds.collection_time_utc
from $diskspace_table_name ds
where ds.host_name = @perfmon_host_name
order by collection_time_utc desc;

;with t_raw as (
	select	fis.collection_time_utc, fis.[database_name], fis.file_logical_name, fis.file_location, fis.database_id, fis.file_id,
			[file_size_mb] = fis.size_on_disk_bytes*1.0/1024/1024
			,[growth_24h_mb] = (fis.size_on_disk_bytes - prv_24h.size_on_disk_bytes)*1.0/1024/1024
			,[growth_5d_mb] = (fis.size_on_disk_bytes - prv_5d.size_on_disk_bytes)*1.0/1024/1024
			,[growth_10d_mb] = (fis.size_on_disk_bytes - prv_10d.size_on_disk_bytes)*1.0/1024/1024
			,[growth_30d_mb] = (fis.size_on_disk_bytes - prv_30d.size_on_disk_bytes)*1.0/1024/1024
	from $fileiostats_table_name fis
	left join $fileiostats_table_name prv_24h
		on prv_24h.collection_time_utc = @_24hour_snapshot and prv_24h.database_name = fis.database_name and prv_24h.file_logical_name = fis.file_logical_name
	left join $fileiostats_table_name prv_5d
		on prv_5d.collection_time_utc = @_5day_snapshot and prv_5d.database_name = fis.database_name and prv_5d.file_logical_name = fis.file_logical_name
	left join $fileiostats_table_name prv_10d
		on prv_10d.collection_time_utc = @_10day_snapshot and prv_10d.database_name = fis.database_name and prv_10d.file_logical_name = fis.file_logical_name
	left join $fileiostats_table_name prv_30d
		on prv_30d.collection_time_utc = @_30day_snapshot and prv_30d.database_name = fis.database_name and prv_30d.file_logical_name = fis.file_logical_name
	where fis.collection_time_utc = @_latest_snap
	"+(case when @database is null then '-- ' else '' end)+"and fis.[database_name] = @database
)
,t_space_summary as (
	select	fis.collection_time_utc, ds.disk_volume, fis.database_name, fis.file_logical_name, fis.file_location, fis.database_id, fis.file_id,
			ds.capacity_mb, ds.free_mb, fis.file_size_mb, fis.growth_24h_mb, fis.growth_5d_mb, fis.growth_10d_mb, fis.growth_30d_mb
	from t_raw fis
	"+(case when @disk_drive is null then 'outer' else 'cross' end)+" apply (
				select top 1 ds.disk_volume, ds.capacity_mb, ds.free_mb from $diskspace_table_name ds
				 where ds.collection_time_utc = @_latest_diskspace_snap and ds.host_name = @perfmon_host_name		 
				 and fis.file_location like (ds.disk_volume+'%')
				 "+(case when @disk_drive is null then '-- ' else '' end)+"and ds.disk_volume = @disk_drive
				 order by len(ds.disk_volume) desc
		) ds
)
select fis.collection_time_utc, fis.database_name, fis.file_logical_name, fis.disk_volume, [file_type] = mf.type_desc, fis.file_location, 
				[disk_capacity_mb] = fis.capacity_mb, [disk_free_mb] = fis.free_mb, fis.file_size_mb, fis.growth_24h_mb, fis.growth_5d_mb, fis.growth_10d_mb, fis.growth_30d_mb
from t_space_summary fis
join sys.master_files mf on mf.database_id = fis.database_id and mf.file_id = fis.file_id
order by fis.database_name, fis.disk_volume, fis.file_logical_name
"
set quoted_identifier on;

