declare @sql nvarchar(max);
declare @params nvarchar(max);
declare @sql_instance varchar(255);
declare @perfmon_host_name varchar(255);
declare @start_time_utc datetime2;
declare @end_time_utc datetime2;
declare @latency_minutes int;
declare @database varchar(255);
declare @replica_server_name varchar(255);
declare @ag_name varchar(255);
declare @ag_listener varchar(255);
declare @replica_type varchar(50);
declare @sync_state varchar(100);
declare @sync_health varchar(100);

set @sql_instance = '$server';
--set @perfmon_host_name = '$perfmon_host_name';
set @start_time_utc = $__timeFrom();
set @end_time_utc = $__timeTo();
set @latency_minutes = case when ltrim(rtrim('$latency_minutes')) <> '' then ltrim(rtrim('$latency_minutes')) else null end; 
set @database = ltrim(rtrim('$database'));
set @replica_server_name = ltrim(rtrim('$replica_server_name'));
set @ag_name = ltrim(rtrim('$ag_name'));
set @ag_listener = ltrim(rtrim('$ag_listener'));
set @replica_type = ltrim(rtrim('$replica_type'));
set @sync_state = ltrim(rtrim('$sync_state_desc'));
set @sync_health = ltrim(rtrim('$sync_health_desc'));

set @params = N'@start_time_utc datetime2, @end_time_utc datetime2, @latency_minutes int, 
                @database varchar(255) = NULL, @replica_server_name varchar(255) = NULL, @ag_name varchar(255) = NULL, 
                @ag_listener varchar(255) = NULL, @replica_type varchar(50) = NULL, @sync_state varchar(100) = NULL, 
                @sync_health varchar(100) = NULL';

set quoted_identifier off;
set @sql = "
set nocount on;
	if object_id('tempdb..#availability_databases') is not null
		drop table #availability_databases;

	select	ar.replica_server_name,
			drs.is_primary_replica,
			adc.database_name,
			ag.name AS ag_name,
			drs.is_local,
			drs.synchronization_state_desc,
			drs.synchronization_health_desc,
			drs.is_suspended,
			last_redone_time_utc = DATEADD(mi, DATEDIFF(mi, getdate(), getutcdate()), drs.last_redone_time),
			drs.log_send_queue_size,
			drs.log_send_rate,
			drs.redo_queue_size,
			drs.redo_rate,
			[estimated_redo_completion_time_min] = case when drs.redo_rate <> 0 then (drs.redo_queue_size / drs.redo_rate) / 60.0 else (drs.redo_queue_size / 1) / 60.0 end,
			last_commit_time_utc = DATEADD(mi, DATEDIFF(mi, getdate(), getutcdate()), drs.last_commit_time),			
			drs.suspend_reason_desc,
			ag.group_id
	into #availability_databases
	from sys.dm_hadr_database_replica_states as drs
	inner join sys.availability_databases_cluster as adc on drs.group_id = adc.group_id
		and drs.group_database_id = adc.group_database_id
	inner join sys.availability_groups as ag on ag.group_id = drs.group_id
	inner join sys.availability_replicas as ar on drs.group_id = ar.group_id
		and drs.replica_id = ar.replica_id
	--order by ag.name, ar.replica_server_name, adc.database_name

	select	replica_server_name,
			is_primary_replica,
			database_name,
			ag_name,
			[ag_listener] = agl.dns_name+' ('+ia.ip_address+')',
			is_local,
			synchronization_state_desc,
			synchronization_health_desc,
			is_suspended,
			latency_seconds = case when is_primary_replica = 1 then 0
									else (	select DATEDIFF(second,ag.last_commit_time_utc,p.last_commit_time_utc) 
											from #availability_databases p 
											where p.is_primary_replica = 1 and p.database_name = ag.database_name
										)
									end,
			redo_queue_size,
			log_send_queue_size,
			last_redone_time_utc,
			log_send_rate,		
			redo_rate,
			estimated_redo_completion_time_min,
			last_commit_time_utc,
			suspend_reason_desc
	from #availability_databases as ag
	left join sys.availability_group_listeners agl on agl.group_id = ag.group_id
	left join sys.availability_group_listener_ip_addresses ia on ia.listener_id = agl.listener_id and ia.state_desc = 'ONLINE'
	where 1=1
  "+(case when @latency_minutes = -1 then '-- ' else '' end)+"and (case when is_primary_replica = 1 then 0 else (	select DATEDIFF(second,ag.last_commit_time_utc,p.last_commit_time_utc) from #availability_databases p where p.is_primary_replica = 1 and p.database_name = ag.database_name	)	end) >= (@latency_minutes*60) 
  "+(case when @database = '__ALL__' then '-- ' else '' end)+"and database_name = @database
  "+(case when @replica_server_name = '__ALL__' then '-- ' else '' end)+"and replica_server_name = @replica_server_name
  "+(case when @ag_name = '__ALL__' then '-- ' else '' end)+"and ag_name = @ag_name
  "+(case when @ag_listener = '__ALL__' then '-- ' else '' end)+"and ag_listener = @ag_listener
  "+(case when @sync_state = '__ALL__' then '-- ' else '' end)+"and synchronization_state_desc = @sync_state
  "+(case when @sync_health = '__ALL__' then '-- ' else '' end)+"and synchronization_health_desc = @sync_health
  "+(case when @replica_type = 'Primary' then '' else '-- ' end)+"and is_primary_replica = 1
  "+(case when @replica_type = 'Secondary' then '' else '-- ' end)+"and is_primary_replica = 0 
  "+(case when @replica_type = 'Local' then '' else '-- ' end)+"and is_local = 1 
	--order by ag.ag_name, ag.replica_server_name, ag.database_name
	order by log_send_queue_size desc, redo_queue_size desc, ag.database_name
"
set quoted_identifier on;

--if (@sql_instance = SERVERPROPERTY('SERVERNAME'))
if ($is_local = 1)
  exec dbo.sp_executesql @sql , @params, @start_time_utc, @end_time_utc, @latency_minutes, @database, @replica_server_name, @ag_name, @ag_listener, @replica_type, @sync_state, @sync_health;
else
  exec [$server].[$dba_db].dbo.sp_executesql @sql , @params, @start_time_utc, @end_time_utc, @latency_minutes, @database, @replica_server_name, @ag_name, @ag_listener, @replica_type, @sync_state, @sync_health;

