set @sync_health = ltrim(rtrim('$sync_health_desc'));

set @params = N'@start_time_utc datetime2, @end_time_utc datetime2, @latency_minutes int, 
                @database varchar(255) = NULL, @replica_server_name varchar(255) = NULL, @ag_name varchar(255) = NULL, 
                @ag_listener varchar(255) = NULL, @replica_type varchar(50) = NULL, @sync_state varchar(100) = NULL, 
                @sync_health varchar(100) = NULL';

set quoted_identifier off;
set @sql = "
set nocount on;
if exists ( select * from dbo.ag_health_state ahs with (nolock)
            where ahs.collection_time_utc = (select max(i.collection_time_utc) from dbo.ag_health_state i) 
            and ahs.latency_seconds >= (@latency_minutes*60) )
begin
  select collection_time_utc, replica_server_name, is_primary_replica, database_name, ag_name, ag_listener, is_local, is_distributed, synchronization_state_desc, synchronization_health_desc, latency_seconds, redo_queue_size, log_send_queue_size, last_redone_time, log_send_rate, redo_rate, estimated_redo_completion_time_min, last_commit_time, is_suspended, suspend_reason_desc
  from dbo.ag_health_state ahs
  where 1=1
  and ahs.collection_time_utc = (select max(i.collection_time_utc) from dbo.ag_health_state i)
  "+(case when @latency_minutes = -1 then '-- ' else '' end)+"and ahs.latency_seconds >= (@latency_minutes*60) 
  "+(case when @database = '__ALL__' then '-- ' else '' end)+"and ahs.database_name = @database
  "+(case when @replica_server_name = '__ALL__' then '-- ' else '' end)+"and ahs.replica_server_name = @replica_server_name
  "+(case when @ag_name = '__ALL__' then '-- ' else '' end)+"and ahs.ag_name = @ag_name
  "+(case when @ag_listener = '__ALL__' then '-- ' else '' end)+"and ahs.ag_listener = @ag_listener
  "+(case when @sync_state = '__ALL__' then '-- ' else '' end)+"and ahs.synchronization_state_desc = @sync_state
  "+(case when @sync_health = '__ALL__' then '-- ' else '' end)+"and ahs.synchronization_health_desc = @sync_health
  "+(case when @replica_type = 'Primary' then '' else '-- ' end)+"and ahs.is_primary_replica = 1
  "+(case when @replica_type = 'Secondary' then '' else '-- ' end)+"and ahs.is_primary_replica = 0 
  "+(case when @replica_type = 'Local' then '' else '-- ' end)+"and ahs.is_local = 1 
  order by log_send_queue_size desc, redo_queue_size desc, database_name
end
ELSE
  select 'No latency based on Filter parameters' as [No Result]
"
set quoted_identifier on;

--if (@sql_instance = SERVERPROPERTY('SERVERNAME'))
if ($is_local = 1)
  exec dbo.sp_executesql @sql , @params, @start_time_utc, @end_time_utc, @latency_minutes, @database, @replica_server_name, @ag_name, @ag_listener, @replica_type, @sync_state, @sync_health;
else
  exec [$server].[$dba_db].dbo.sp_executesql @sql , @params, @start_time_utc, @end_time_utc, @latency_minutes, @database, @replica_server_name, @ag_name, @ag_listener, @replica_type, @sync_state, @sync_health;

