order by total_reserved_GB desc
offset ((@page_no-1)*@page_size) rows fetch next @page_size rows only;

select 	--collection_time = DATEADD(mi, DATEDIFF(mi, getdate(), getutcdate()), bi.run_datetime),
				bi.database_name, [object_name] = quotename(bi.schema_name)+'.'+quotename(bi.table_name), 
				/* bi.index_name, bi.object_type, */ 
				bi.index_size_summary, bi.index_definition,
				bi.index_usage_summary, bi.index_op_stats, --bi.total_rows, bi.total_reserved_MB, bi.total_reserved_LOB_MB,
				bi.data_compression_desc, bi.create_date, bi.more_info
from ${blitzindex_table_name} bi
inner join #delta_tables dt
	on bi.run_datetime = (select max(i.run_datetime) from ${blitzindex_table_name} i)
	and bi.database_name = dt.database_name and bi.schema_name = dt.schema_name and bi.table_name = dt.table_name
	and bi.index_id <= 1
order by bi.total_reserved_MB desc, bi.total_rows desc
