;WITH [Stats] AS
(
	SELECT	[collection_time_utc],
			[database_name],
			--[database_id],
			[file_logical_name],
			--[file_id],
			[file_location],
			[sample_ms_delta],
			--[num_of_reads],
			[read_write_bytes_delta] = case when [read_write_bytes_delta] < 0 then 0 else [read_write_bytes_delta] end,
			[read_bytes_delta],
			--[io_stall_read_ms],
			--[io_stall_queued_read_ms],
			--[num_of_writes],
			[writes_bytes_delta],
			--[io_stall_write_ms],
			--[io_stall_queued_write_ms],
			--[io_stall],
			[io_stall_delta],
			--[size_on_disk_bytes],
			--[io_pending_count],
			--[io_pending_ms_ticks_total],
			--[io_pending_ms_ticks_avg],
			--[io_pending_ms_ticks_max],
			--[io_pending_ms_ticks_min]
			[StatsRank] = ROW_NUMBER() OVER(PARTITION BY [collection_time_utc] ORDER BY read_write_bytes_delta DESC),
			[Percentage] = ((100.0 * [read_write_bytes_delta]) / (SUM ([read_write_bytes_delta]) OVER (PARTITION BY [collection_time_utc]))),
			[PercentageTotal] = ( (100.0 * ( SUM([read_write_bytes_delta]) 
											 OVER(PARTITION BY [collection_time_utc] 
											 ORDER BY [read_write_bytes_delta] DESC, [io_stall_delta] desc) )
								  ) / (SUM ([read_write_bytes_delta]) OVER (PARTITION BY [collection_time_utc])) )
	FROM dbo.[vw_file_io_stats_deltas] AS [Stats]
	WHERE collection_time_utc between @start_time_utc and @end_time_utc
	"+(case when @database is null then '-- ' else '' end)+"AND [Stats].[database_name] = @database
	"+(case when @disk_drive is null then '-- ' else '' end)+"and [Stats].disk_volume = @disk_drive
)
SELECT	time = [collection_time_utc]
		--,[database_name]
		,[metric] =  [database_name]+ ' (__ '+[file_logical_name]+' __)'
		--,[file_logical_name]
		--,[file_location]
		--,[sample_ms_delta]
		--,[read_write_bytes_delta]
		--,[read_bytes_delta]
		--,[writes_bytes_delta]
		--,[StatsRank]
		--,[Percentage]
		--,[PercentageTotal]
		,[value] = [read_write_bytes_delta]
FROM [Stats] as cur
WHERE [StatsRank] <= @FileIOStatsTop
AND [PercentageTotal] <= @StatsPercentTop
ORDER BY [time] ASC, [read_write_bytes_delta] DESC, [io_stall_delta] DESC
OPTION(RECOMPILE);
