	from #file_io_stats fis
)
select	[time] = fis.collection_time_utc, [metric] = fis.database_name, 
				[value] = case when fis.[num_of_bytes_read_written] > fis.[num_of_bytes_read_written_previous] 
												then fis.[num_of_bytes_read_written] - fis.[num_of_bytes_read_written_previous]
												else 0 end
